function validate()
{
    validateEmail();
}
function validateEmail()
{
    if (email[0] !== email[0].toLowerCase()) {
        alert("First letter must be lowercase.");
        return;   
      }

      let hasNumber = false;
      let hasSpecialChar = false;
      // Loop through each character in the email
      for (let i = 0; i < email.length; i++) {
        const char = email[i];

        // Check if the character is a number
        if (!isNaN(char)) {
          hasNumber = true;
        }
        // Check if the character is a special character
        if ("@!#$%^&*()_+{}[]:;<>,.?~\\/-".includes(char)) {
          hasSpecialChar = true;
        }
      }
}
const handle = (e) => {
    if(validate()){
        e.preventDefault();
        const user = { email, firstName, lastName, mobileNumber, dob, address }
        if (id) {
            EmployeeService.updateRecords(parseInt(id), user).then((response) => {
            <link to="/user"></link>;
            }).catch(error => {
                console.log("update err" + error);
            })
        }
        else {
            EmployeeService.saveRecords(user).then((response) => {
                console.log(response.data);
                <link to="/user"></link>;
            }).catch(error => {
                console.log("Error is" + error)
            })
        }
}
}

