import React,{useState,useEffect} from "react";
import './LoginStyle.css';
import { Link, useParams } from "react-router-dom";
import EmployeeService from "./services/EmployeeService";
const LoginForm = () =>
{
const[firstName,setFirstName]=useState("");
const[lastName,setLastName]=useState("");
const[email,setEmail]=useState("");
const[mobileNumber,setMobilenumber]=useState("");
const[dob,setDOB]=useState("");
const[address,setAddress]=useState("");
const {id} =useParams();
function onClick(e)
{
e.preventDefault();
handle();
}
// const handle=(e)=>{

// const employee={firstName , lastName,email, dob,mobileNumber,address}
// // console.log("5555");
// // console.log(firstName);
// // console.log(employee);
// EmployeeService.saveRecords(employee).then((response) =>{
// console.log(response.data)
// }).catch(error => {
// console.log(error)
// })
function validate()
{
    validateFirstName();
    validateLastName();
    validateEmail();
    validateMobilenumber();
    validateAddress();
    validateDOB();
}

const emailValidate = document.getElementById("email").value;
const firstNameValidate = document.getElementById("firstName").value;
const lastNameValidate= document.getElementById("lastName").value;
const mobileValidate = document.getElementById("mobile").value;
const dobValidate = document.getElementById("dob").value;
const addressValidate = document.getElementById("address").value;
function validateFirstName()
{
    if (!/^[a-zA-Z]/.test(firstNameValidate)){
        firstNameError.textContent = "First name must start with a letter";
      } else {
        firstNameError.textContent = "";
      }
}
const handle = (e) => {
    if(validate()){
        e.preventDefault();
        const user = { email, firstName, lastName, mobileNumber, dob, address }
        if (id) {
            EmployeeService.updateRecords(parseInt(id), user).then((response) => {
            <link to="/user"></link>;
            }).catch(error => {
                console.log("update err" + error);
            })
        }
        else {
            EmployeeService.saveRecords(user).then((response) => {
                console.log(response.data);
                <link to="/user"></link>;
            }).catch(error => {
                console.log("Error is" + error)
            })
        }
    }

}

const validateDOB=()=>{
    const today = new Date();
    const selectedDate = new Date(this.state.dob);
     if (selectedDate > today) {
    return setDOB("fghjk");
  }
  else{
    return selectedDate;
  }
}

useEffect(() => {
    console.log("dfghj")
    console.log(id)
  EmployeeService.getUserById(id).then((response) => {
    console.log("rtyk")
        setFirstName(response.data.firstName)
        setLastName(response.data.lastName)
        setEmail(response.data.email)
        setMobilenumber(response.data.mobileNumber)
        setAddress(response.data.address)
        setDOB(response.data.dob)
      }).catch(error => {
        console.log("user" + error)
    })
}, [])
return(
<>
<form >
<div class="top">
<img id="img" src="https://divum.in/images/logo/Divum_Logo%20SVG%20(2).svg"/>
</div>
<div class="front-view">
<div class="head">
<h1><b>LOGIN FORM</b></h1>
<div class="body">
<label>FIRSTNAME:</label>
<input type="text" value={firstName} 
onChange={(e)=>setFirstName(e.target.value)} 
placeholder="Enter your firstname"></input><br></br><br></br>
<label>LASTNAME :</label>
<input type="text" value={lastName} onChange={(e)=>setLastName(e.target.value)} placeholder="Enter your lastname"></input><br></br><br></br>
<label>EMAIL :</label>
<input type="text" value={email} onChange={(e)=>setEmail(e.target.value)} placeholder="Enter your email"></input><br></br><br></br>
<label>MOBILENUMBER :</label>
<input type="number" value={mobileNumber}onChange={(e)=>setMobilenumber(e.target.value)}placeholder="Enter your MobileNo"></input><br></br><br></br>
<label>DOB :</label>
<input type="DATE" value={dob} onChange={(e)=>setDOB(e.target.value)} placeholder="Enter your DOB"></input><br></br><br></br>
<label>ADDRESS :</label><br></br>
<textarea value={address} onChange={(e)=>setAddress(e.target.value)} placeholder="Enter your Address" rows="3" cols="50"></textarea><br></br><br></br>
<div class="btn">
<button type="submit" onClick={(e)=>handle(e)}><Link to="/user">Submit</Link></button>
</div>
</div>
</div>
</div> 
</form>
</>
)
}
export default LoginForm;
