//             if(!validateEmail(e.target.value))
//             setError(emailAddress,"Email id invalid");
//             else
//             setSuccess(emailaddress);
//     }


// }

useEffect(() => {
    console.log("dfghj")
    console.log(id)
  EmployeeService.getUserById(id).then((response) => {
    console.log("rtyk")
        setFirstName(response.data.firstName)
        setLastName(response.data.lastName)
        setEmail(response.data.email)
        setMobilenumber(response.data.mobileNumber)
        setAddress(response.data.address)
        setDOB(response.data.dob)
      }).catch(error => {
        console.log("user" + error)
    })
}, [])

function setError(element, message) 
{        
    const inputGroup = element.parentElement;        
    const errorGroup = inputGroup.querySelector('.error');        
    errorGroup.innerText = message;        
    inputGroup.classList.add('error')        
    inputGroup.classList.remove('success')   
 }    
function setSuccess(element) 
{        
    const inputGroup = element.parentElement;        
    const errorGroup = inputGroup.querySelector('.error');        
    errorGroup.innerText = "";        
    inputGroup.classList.add('success')        
    inputGroup.classList.remove('error')  
 }



const fname=document.querySelector('#fname')
const email1=document.querySelector('#email')
const valueSet=(e)=>{
    switch (e.target.value){
        case 'fname':
            if(e.target.value==''){
                setError(fname,"Firstname is required")
            }
            else{
                setSuccess(fname);
            }
            break;

        case 'email1':
            if(e.target.value==''){
                setError(email1,"email is required")
            }
            else{
                setSuccess(email1);
            }
            break;
    }
}
return(
<>
<form >
<div class="top">
<img id="img" src="https://divum.in/images/logo/Divum_Logo%20SVG%20(2).svg"/>
</div>
<div class="front-view">
<div class="head">
<h1><b>LOGIN FORM</b></h1>
<div class="body">
<label>FIRSTNAME:</label>
<input type="text" id="fname" value={firstName} 
onChange={(e)=>setFirstName(e.target.value)} 
placeholder="Enter your firstname"></input><br></br><br></br>
<label>LASTNAME :</label>
<input type="text" value={lastName} onChange={valueSet} id="lname" placeholder="Enter your lastname"></input><br></br><br></br>
<label>EMAIL :</label>
<input type="text" value={email} onChange={(e)=>setEmail(e.target.value)} id="email" placeholder="Enter your email"></input><br></br><br></br>
<label>MOBILENUMBER :</label>
<input type="number" value={mobileNumber}onChange={(e)=>setMobilenumber(e.target.value)} id="num" placeholder="Enter your MobileNo"></input><br></br><br></br>
<label>DOB :</label>
<input type="DATE" value={dob} onChange={(e)=>setDOB(e.target.value)} id="date" placeholder="Enter your DOB"></input><br></br><br></br>
<label>ADDRESS :</label><br></br>
<textarea value={address} onChange={(e)=>setAddress(e.target.value)} placeholder="Enter your Address" id="address" rows="3" cols="50"></textarea><br></br><br></br>
<div class="btn">
<button type="submit" onClick={(e)=>handle(e)}><Link to="/user">Submit</Link></button>
</div>
</div>
</div>
</div> 
</form>
</>
);
}
export default LoginForm;
