import React,{useState,useEffect} from "react";
import './LoginStyle.css';
import { Link, useParams } from "react-router-dom";
import EmployeeService from "./services/EmployeeService";
const LoginForm = () =>
{
const[firstName,setFirstName]=useState("");
const[lastName,setLastName]=useState("");
const[email,setEmail]=useState("");
const[mobileNumber,setMobilenumber]=useState("");
const[dob,setDOB]=useState("");
const[address,setAddress]=useState("");
const {id} =useParams();
function onClick(e)
{
e.preventDefault();
handle();
}
const handle = (e) => {
        e.preventDefault();
        const user = { email, firstName, lastName, mobileNumber, dob, address }
        if (id) {
            EmployeeService.updateRecords(parseInt(id), user).then((response) => {
            <link to="/user"></link>;
            }).catch(error => {
                console.log("update err" + error);
            })
        }
        else {
            EmployeeService.saveRecords(user).then((response) => {
                console.log(response.data);
                <link to="/user"></link>;
            }).catch(error => {
                console.log("Error is" + error)
            })
        }
} 

const validateEmail=(email1)=>{
    const emailPattern =  /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailPattern.match(email);
}      
