import React, { useState, useEffect } from "react";
import './LoginStyle.css';
import { Link, useParams } from "react-router-dom";
import EmployeeService from "./services/EmployeeService";
const LoginForm = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [mobileNumber, setMobilenumber] = useState("");
    const [dob, setDOB] = useState("");
    const [address, setAddress] = useState("");
    const { id } = useParams();


    let fname;
    let email1 ;
    const lname = document.querySelector('#lname')
    const mobile = document.querySelector('#mobile')
    const add = document.querySelector('#address')




    function onClick(e) {
        e.preventDefault();
        handle();
    }
    const handle = (e) => {
        e.preventDefault();
        const user = { email, firstName, lastName, mobileNumber, dob, address }
        if (id) {
            EmployeeService.updateRecords(parseInt(id), user).then((response) => {
                <link to="/user"></link>;
            }).catch(error => {
                console.log("update err" + error);
            })
        }
        else {
            EmployeeService.saveRecords(user).then((response) => {
                console.log(response.data);
                <link to="/user"></link>;
            }).catch(error => {
                console.log("Error is" + error)
            })
        }
    }

    const validateEmail = (email) => {
        const emailPattern = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailPattern.test(email);
    }

    const validateMobile = (mobile) => {
        const mobpattern = /^[0-9]{10}/;
        return mobile.match(mobpattern);
    }
    // const validateDOB=()=>{

    //     const today = new Date();
    //     const selectedDate = new Date(this.state.dob);
    //      if (selectedDate > today) {
    //     return setDOB("fghjk");
    //   }
    //   else{
    //     return selectedDate;
    //   }
    // }


    // const setValues=(e) =>
    // {
    //     switch(e.target.name)
    //     {
    //         case 'email':
    //             setEmail(e.target.value);
    //             if(!validateEmail(e.target.value))
    //             setError(emailAddress,"Email id invalid");
    //             else
    //             setSuccess(emailaddress);
    //     }


    // }

    useEffect(() => {
        console.log("dfghj")
        console.log(id)
        EmployeeService.getUserById(id).then((response) => {
            console.log("rtyk")
            setFirstName(response.data.firstName)
            setLastName(response.data.lastName)
            setEmail(response.data.email)
            setMobilenumber(response.data.mobileNumber)
            setAddress(response.data.address)
            setDOB(response.data.dob)
        }).catch(error => {
            console.log("user" + error)
        })
    }, [])

    function setError(element, message) {
        //console.log(element);
        const inputGroup = element.parentElement;
        const errorGroup = inputGroup.querySelector('.error');
        errorGroup.innerText = message;
        inputGroup.classList.add('error')
        inputGroup.classList.remove('success')
    }
    function setSuccess(element) {
        const inputGroup = element.parentElement;
        const errorGroup = inputGroup.querySelector('.error');
        errorGroup.innerText = "";
        inputGroup.classList.add('success')
        inputGroup.classList.remove('error')
    }




    const valueSet = (e) => {
        switch (e.target.name) {
            case 'fname':
                fname=document.querySelector('#fname');
                setFirstName(e.target.value);

            case 'lname':
                setLastName(e.target.value);
                // if(e.target.value==''){
                //     setError(fname,"Lastname is required")
                // }
                // else if(!validateName){
                //     setError(fname,"Lastname is invalid")
                // }
                // else{
                //     setSuccess(fname);
                // }
                break;
            case 'mobile':
                setMobilenumber(e.target.value);
                if (e.target.value == '') {
                    setError(mobile, "mobile number is required")
                }
                else if (!validateMobile(email1)) {
                    setError(mobile, "mobile number is invalid")
                }
                else {
                    setSuccess(mobile)
                }
                break;

            case 'address':
                setAddress(e.target.value);

            case 'email':
                email1=document.querySelector('#email');

                setEmail(e.target.value);
                if (e.target.value == '') {
                    setError(email1, "email is required")
                }
                else if (!validateEmail(email)) {
                    setError(email1, "email is invalid")
                }
                else {
                    setSuccess(email1)
                }
                break;
        }
    }
    return (
        <>
            <form >
                <div class="top">
                    <img id="img" src="https://divum.in/images/logo/Divum_Logo%20SVG%20(2).svg" />
                </div>
                <div class="front-view">
                    <div class="head">
                        <h1><b>LOGIN FORM</b></h1>
                        <div class="body">
                            <div>
                            <label>FIRSTNAME:</label>
                            <input type="text" name="fname" id="fname" value={firstName}
                                onChange={(e) => setFirstName(e.target.value)}
                                placeholder="Enter your firstname"></input>
                                <div className="error"></div>
                                <br></br><br></br></div>

                            <div>
                            <label>LASTNAME :</label>
                            <input type="text" value={lastName} onChange={valueSet} id="lname" name='lname' placeholder="Enter your lastname"></input><br></br><br></br>
                            <div className="error"></div></div>

                            <div>
                                <label>EMAIL :</label>
                                <input type="text" value={email} onChange={valueSet} id="email" name="email" placeholder="Enter your email"></input>
                                <div className="error"></div>
                                <br></br><br></br>
                            </div>

                              <div>
                              <label>MOBILENUMBER :</label>
                             <input type="text" value={mobile} onChange={valueSet} id="mobile" name="mobile" placeholder="Enter your MobileNo"></input><br></br><br></br>
                             <div className="error"></div>
                              </div>

                              <div>
                              <label>DOB :</label>
                            <input type="DATE" value={dob} placeholder='dd-mm-yyy' onChange={(e) => setDOB(e.target.value)} id="date"></input><br></br><br></br>

                              </div>
                             <label>ADDRESS :</label><br></br>
                            <textarea value={address} onChange={(e) => setAddress(e.target.value)} maxLength="50" placeholder="Enter your Address" id="address" rows="3" cols="50"></textarea><br></br><br></br>
                            <div class="btn">
                                <button type="submit" onClick={(e) => handle(e)}><Link to="/user">Submit</Link></button>
                            </div>
                        </div>
                    </div>
                </div>
            </form>
        </>
    );
}
export default LoginForm;
