import React, { useState, useEffect } from "react";
import './LoginStyle.css';
import { Link, useParams } from "react-router-dom";
import EmployeeService from "./services/EmployeeService";
const LoginForm = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [mobileNumber, setMobilenumber] = useState("");
    const [dob, setDOB] = useState("");
    const [address, setAddress] = useState("");
    const [overAll, setOverAll] = useState("");
    const { id } = useParams();


    let fname;
    let email1 ;
    let lname;
    let DateOfBirth;
    let mobile;

    function onClick(e) {
        e.preventDefault();
        handle();
    }
    const handle = (e) => {
        e.preventDefault();
        const user = {email, firstName, lastName, mobileNumber, dob, address };
        if (id) {
            console.log("UPDATE");
            console.log( "Id: "+ id);
            console.log("Object: "+user);
            EmployeeService.updateRecords(id, user).then((response) => {
                <link to="/user"></link>;
            }).catch(error => {<link to="/user"></link>;
                console.log("update err" + error);
            })
        }
        else {
            console.log("INSERT");
            console.log("Object: "+user);
            EmployeeService.saveRecords(user).then((response) => {
                console.log(response.data);
                <link to="/user"></link>;
            }).catch(error => {
                console.log("Error is" + error)
            })
        }
    }

    const validateEmail = (email) => {
        const emailPattern = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return emailPattern.test(email);
    //     if(email !== ""){
    //         test(email);
    //     }
    // }
    }
    const validateMobile = (mobile) => {


        const mobpattern = /^[0-9]{10}/;
        return mobpattern.test(mobile);

    }

    const validateFname = (firstName) => {
        const namePattern =/^[A-Za-z]+$/;
        return namePattern.test(firstName);
    }
    const validateLname = (lastName) => {
        const namePattern = /^[A-Za-z]+$/;
        return namePattern.test(lastName);
    }
    // const validateDOB=()=>{

    //     const today = new Date();
    //     const selectedDate = new Date(this.state.dob);
    //      if (selectedDate > today) {
    //     return setDOB("fghjk");
    //   }
    //   else{
    //     return selectedDate;
    //   }
    // }


    // const setValues=(e) =>
    // {
    //     switch(e.target.name)
    //     {
    //         case 'email':
    //             setEmail(e.target.value);
    //             if(!validateEmail(e.target.value))
    //             setError(emailAddress,"Email id invalid");
    //             else
    //             setSuccess(emailaddress);
    //     }


    // }
    useEffect(()=>
    {

    })

    useEffect(() => {

        EmployeeService.getUserById(id).then((response) => {
            // console.log("rtyk",response,response.data.mobileNumber);
            setOverAll(response);
        }).catch(error => {
            console.log("user" + error)
        })
    }, [])

    useEffect(()=>{
        setFirstName(overAll?.data?.firstName)
        setLastName(overAll?.data?.lastName)
        setEmail(overAll?.data.email)
        setMobilenumber(overAll.data.mobileNumber)
        setAddress(overAll.data.address)
        setDOB(overAll.data.dob)
    },[overAll])

    function setError(element, message) {
        //console.log(element);
        const inputGroup = element.parentElement;
        const errorGroup = inputGroup.querySelector('.error');
        errorGroup.innerText = message;
    }
    function setSuccess(element) {
        const inputGroup = element.parentElement;
        const errorGroup = inputGroup.querySelector('.error');
        errorGroup.innerText = "";
    }

    const valueSet = (e) => {
        switch (e.target.name) {
            case 'fname':
                fname=document.querySelector('#fname');
                setFirstName(e.target.value);
                if(e.target.value==''){
                    setError(fname,"Firstname is required")
                }
                else if(!validateFname(e.target.value)){
                    setError(fname,"Firstname is invalid")
                }
                else{
                    setSuccess(fname);
                }
                break;

            case 'lname':
                lname=document.querySelector('#lname');
                setLastName(e.target.value);
                if(e.target.value==''){
                    setError(lname,"Lastname is required")
                }
                else if(!validateLname(e.target.value)){
                    setError(lname,"Lastname is invalid")
                }
                else{
                    setSuccess(lname);
                }
                break;

            case 'mobile':
                mobile=document.querySelector('#mobile');
                setMobilenumber(e.target.value);
                if (e.target.value == '') {
                    setError(mobile, "mobile number is required")
                }
                else if (!validateMobile(e.target.value)) {
                    setError(mobile, "mobile number is invalid")
                }
                else {
                    setSuccess(mobile)
                }
                break;

            case 'email':
                email1=document.querySelector('#email');

                setEmail(e.target.value);
                if (e.target.value == '') {
                    setError(email1, "email is required")
                }
                else if (!validateEmail(email)) {
                    setError(email1, "email is invalid")
                }
                else {
                    setSuccess(email1)
                }
                break;

                case 'dob':
                    DateOfBirth=document.querySelector('#date');
                    setDOB(e.target.value);
                        const inputDate = new Date(dob);
                        const currentDate = new Date();
                        if (inputDate < currentDate) {
                            console.log('success');
                            setSuccess(DateOfBirth);
                        } else {
                            console.log('fail');
                            setError(DateOfBirth,"Date cannot be in Future Date");
                        }

        }

    }
    return (
        <>
            <form >
                <div class="top">
                    <img id="img" src="https://divum.in/images/logo/Divum_Logo%20SVG%20(2).svg" />
                </div>
                <div class="front-view">
                    <div class="head">
                        <h1><b>LOGIN FORM</b></h1>
                        <div class="body">
                            <div>
                            <label>FIRSTNAME:</label>
                            <input type="text" name="fname" id="fname" value={firstName}
                                onChange={valueSet}  autoComplete="off"
                                placeholder="Enter your firstname"></input>
                                <div className="error"></div>
                                <br></br><br></br></div>

                            <div>
                            <label>LASTNAME :</label>
                            <input type="text" value={lastName} onChange={valueSet} id="lname" autoComplete="off" name='lname' placeholder="Enter your lastname"></input><br></br><br></br>
                            <div className="error"></div></div>

                            <div>
                                <label>EMAIL :</label>
                                <input type="text" value={email} onChange={valueSet} id="email" name="email" autoComplete="off" placeholder="Enter your email"></input>
                                <div className="error"></div>
                                <br></br><br></br>
                            </div>

                              <div>
                              <label>MOBILENUMBER :</label>
                             <input type="text" value={mobileNumber} onChange={valueSet} id="mobile" name="mobile" autoComplete="off" maxLength="10" placeholder="Enter your MobileNo"></input><br></br><br></br>
                             <div className="error"></div>

                              </div>

                              <div>
                            <label>DOB :</label>
                            <input type="date" placeholder="dd-mm-yyyy" value={dob} onChange={valueSet}  autoComplete="off"name="dob" id="date"></input><br></br><br></br>
                            <div className="error"></div>
                              </div>


                              <div>
                              <label>ADDRESS :</label><br></br>
                            <textarea value={address} onChange={(e) => setAddress(e.target.value)} autoComplete="off" maxLength="50" placeholder="Enter your Address" id="address" rows="3" cols="50"></textarea><br></br><br></br>
                            <div className="error"></div>
                              </div>

                              <div class="btn">
                                <button type="submit" onClick={(e) => handle(e)}><Link to="/user">Submit</Link></button>
                            </div>
                        </div>
                    </div>
                </div>
            </form>
        </>
    );
}
export default LoginForm;
